var Login = function() {

    jQuery.validator.addMethod("confirmVerifyCode", function(value, element) {
        $.ajax({
            url : contextPath + "/verifyCode/" + value,// 请求的URL
            type : "post",
            async : false,
            success : function(response) {

            }
        });
        var result = $(element).val();
        if (result == "true") {
            return this.optional(element) || true;
        } else {
            return this.optional(element) || false;
        }

    })

    var handleLogin = function() {

        var $loginForm = $('.login-form');
        console.info($("#verifyCodeImg"));
        
        $(document).on("click", "#verifyCodeImgBtn", function() {
            $("#verifyCodeImg")[0].src = contextPath + "/verifyCode?" + Math.random();
            $('input[name=verifyCode]', $loginForm).val("");
            $loginForm.valid()
        });

        $loginForm.validate({
            errorElement : 'span', // default input error message container
            errorClass : 'help-block', // default input error message class
            focusInvalid : false, // do not focus the last invalid input
            rules : {
                username : {
                    required : true
                },
                password : {
                    required : true
                },
                verifyCode : {
                    required : true,
                    rangelength : [ 4, 6 ],
                    remote : {
                        url : contextPath + "/verifyCode/",
                        type : "post",

                    }
                // confirmVerifyCode : true
                },
                remember : {
                    required : false
                }
            },
            messages : {
                username : {
                    required : "请输入用户名."
                },
                password : {
                    required : "请输入密码."
                },
                verifyCode : {
                    required : "请输入验证码.",
                    rangelength : "验证码不正确"
                // confirmVerifyCode : "验证码不正确"
                }
            },
            errorPlacement : function(error, element) {
                var $inputGroup = element.closest(".input-group");
                if (isInputGroup(element)) {
                    error.insertAfter($inputGroup);
                    $(element).next("span").addClass("glyphicon-remove").removeClass("glyphicon-ok");
                } else {
                    error.insertAfter(element.closest('.input-icon'));
                }
            },
            invalidHandler : function(event, validator) {
                var $loginAlert = $('.alert-danger', $('.login-form'));
                var $loginHintSpan = $loginAlert.find("span");
                $loginHintSpan.text("请输入用户名和密码!")
                // $loginAlert.show();
            },

            highlight : function(element, errorClass, validClass) { // hightlight
                $(element).closest('.form-group').addClass('has-error'); // set
            },

            unhighlight : function(element, errorClass, validClass) {

            },

            success : function(label, element) {
                // console.info("success");
                // console.info(label);
                var $formGroup = label.closest('.form-group');
                $formGroup.addClass("has-success").removeClass('has-error');
                label.remove();
                if (isInputGroup(element)) {
                    $(element).next("span").remove();
                    $("<span class='glyphicon glyphicon-ok form-control-feedback'></span>").insertAfter($(element));
                }
            },

            // TODO
            submitHandler : function(form) {
                var $form = $(form);
                var $loginAlert = $('.alert-danger', $('.login-form'));
                var $loginHintSpan = $loginAlert.find("span");
                var params = $form.serializeObject();
                var loginLogParams = {
                    loginIp : "",
                    loginBrowser : "",
                    screenResolution : "",
                    operatingSystem : ""
                }
                $.ajax({
                    url : contextPath + "/login",
                    type : "post",
                    data : params,
                    success : function(response) {
                        $('input[name=verifyCode]', $loginForm).val("");
                        if (response.success) {
                            window.location = contextPath + "/index";
                        } else {
                            $loginHintSpan.text(response.msg);
                            $loginForm.valid();
                            $loginAlert.show();
                        }
                    }
                })
            }
        });

        $('.login-form input').keypress(function(e) {
            if (e.which == 13) {
                if ($('.login-form').validate().form()) {
                    $('.login-form').submit(); // form validation success, call
                    // ajax form submit
                }
                return false;
            }
        });

        function isInputGroup(element) {
            var $inputGroup = $(element).closest(".input-group");
            var size = $inputGroup.size();
            return size >= 1;
        }

    }

    var handleForgetPassword = function() {
        $('.forget-form').validate({
            errorElement : 'span', // default input error message container
            errorClass : 'help-block', // default input error message class
            focusInvalid : false, // do not focus the last invalid input
            ignore : "",
            rules : {
                email : {
                    required : true,
                    email : true
                }
            },

            messages : {
                email : {
                    required : "Email is required."
                }
            },

            invalidHandler : function(event, validator) { // display error
                // alert on form
                // submit

            },

            highlight : function(element) { // hightlight error inputs
                $(element).closest('.form-group').addClass('has-error'); // set
                // error
                // class
                // to
                // the
                // control
                // group
            },

            success : function(label) {
                label.closest('.form-group').removeClass('has-error');
                label.remove();
            },

            errorPlacement : function(error, element) {
                error.insertAfter(element.closest('.input-icon'));
            },

            submitHandler : function(form) {
                form.submit();
            }
        });

        $('.forget-form input').keypress(function(e) {
            if (e.which == 13) {
                if ($('.forget-form').validate().form()) {
                    $('.forget-form').submit();
                }
                return false;
            }
        });

        jQuery('#forget-password').click(function() {
            jQuery('.login-form').hide();
            jQuery('.forget-form').show();
        });

        jQuery('#back-btn').click(function() {
            jQuery('.login-form').show();
            jQuery('.forget-form').hide();
        });

    }

    var handleRegister = function() {

        function format(state) {
            if (!state.id) {
                return state.text;
            }
            var $state = $('<span><img src="../assets/global/img/flags/' + state.element.value.toLowerCase() + '.png" class="img-flag" /> '
                    + state.text + '</span>');

            return $state;
        }

        if (jQuery().select2 && $('#country_list').size() > 0) {
            $("#country_list").select2({
                placeholder : '<i class="fa fa-map-marker"></i>&nbsp;Select a Country',
                templateResult : format,
                templateSelection : format,
                width : 'auto',
                escapeMarkup : function(m) {
                    return m;
                }
            });

            $('#country_list').change(function() {
                $('.register-form').validate().element($(this)); // revalidate
                // the
                // chosen
                // dropdown
                // value and
                // show
                // error or
                // success
                // message
                // for the
                // input
            });
        }

        $('.register-form').validate({
            errorElement : 'span', // default input error message container
            errorClass : 'help-block', // default input error message class
            focusInvalid : false, // do not focus the last invalid input
            ignore : "",
            rules : {

                fullname : {
                    required : true
                },
                email : {
                    required : true,
                    email : true
                },
                address : {
                    required : true
                },
                city : {
                    required : true
                },
                country : {
                    required : true
                },

                username : {
                    required : true
                },
                password : {
                    required : true
                },
                rpassword : {
                    equalTo : "#register_password"
                },

                tnc : {
                    required : true
                }
            },

            messages : { // custom messages for radio buttons and checkboxes
                tnc : {
                    required : "Please accept TNC first."
                }
            },

            invalidHandler : function(event, validator) { // display error
                // alert on form
                // submit

            },

            highlight : function(element) { // hightlight error inputs
                $(element).closest('.form-group').addClass('has-error'); // set
                // error
                // class
                // to
                // the
                // control
                // group
            },

            success : function(label) {
                label.closest('.form-group').removeClass('has-error');
                label.remove();
            },

            errorPlacement : function(error, element) {
                if (element.attr("name") == "tnc") { // insert checkbox
                    // errors after the
                    // container
                    error.insertAfter($('#register_tnc_error'));
                } else if (element.closest('.input-icon').size() === 1) {
                    error.insertAfter(element.closest('.input-icon'));
                } else {
                    error.insertAfter(element);
                }
            },

            submitHandler : function(form) {
                form[0].submit();
            }
        });

        $('.register-form input').keypress(function(e) {
            if (e.which == 13) {
                if ($('.register-form').validate().form()) {
                    $('.register-form').submit();
                }
                return false;
            }
        });

        jQuery('#register-btn').click(function() {
            jQuery('.login-form').hide();
            jQuery('.register-form').show();
        });

        jQuery('#register-back-btn').click(function() {
            jQuery('.login-form').show();
            jQuery('.register-form').hide();
        });
    }

    return {
        // main function to initiate the module
        init : function() {

            handleLogin();
            handleForgetPassword();
            handleRegister();

        }

    };

}();

jQuery(document).ready(function() {
    Login.init();

    $(document).ajaxStart(function() {
        App.blockUI({
            boxed : true,
            message : "用户认证中..."
        });
    }).ajaxStop(function() {
        App.unblockUI();
    });
});